import React, { useState } from 'react';
import {
    FaTh,
    FaUserAlt,
    FaRegChartBar,
    FaCommentAlt,
    FaShoppingBag,
    FaThList
} from "react-icons/fa";
import { NavLink, useLocation } from 'react-router-dom';

const Sidebar = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);

    const location = useLocation();

    const menuItem = [
        {
            path: "/",
            name: "Dashboard",
            icon: <FaTh />
        },
        {
            path: "/about",
            name: "About",
            icon: <FaUserAlt />
        },
        {
            path: "/analytics",
            name: "Analytics",
            icon: <FaRegChartBar />
        },
        {
            path: "/comment",
            name: "Comment",
            icon: <FaCommentAlt />
        },
        {
            path: "/product",
            name: "Product",
            icon: <FaShoppingBag />
        },
        {
            path: "/productList",
            name: "Product List",
            icon: <FaThList />
        }
    ];


    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
            {/* Navbar */}

            {/* Sidebar and Main Content */}
            <div style={{ display: 'flex', flex: 1 }}>
                <div
                    style={{
                        width: isOpen ? '259px' : '50px',
                        height: '100%',
                        background: 'white',
                        boxShadow: '3px -3px 20px rgba(0, 0, 0, 0.20) inset',
                        position: 'relative',
                        transition: 'width 0.3s',
                    }}
                >
                    <div style={{ padding: '16px', display: 'flex', alignItems: 'center' }}>
                        <img
                            style={{ width: '182px', height: '91px', display: isOpen ? 'block' : 'none' }}
                            src="https://via.placeholder.com/182x91"
                            alt="Logo"
                        />
                    </div>
                    <div>
                        {menuItem.map((item, index) => (
                            <NavLink
                                to={item.path}
                                key={index}
                                style={{
                                    textDecoration: 'none',
                                    display: 'flex',
                                    alignItems: 'center',
                                    padding: '10px 30px',
                                    color: 'white',
                                    fontSize: '20px',
                                    fontFamily: 'JioType Var',
                                    fontWeight: '400',
                                    background: 'linear-gradient(90deg, #00A450 0%, #003E1E 100%)',
                                    marginBottom: '10px',
                                    borderRadius: '4px',
                                    transition: 'all 0.3s',
                                    gap: isOpen ? '10px' : '0',
                                }}
                                activeClassName="active"
                            >
                                <div style={{ fontSize: '1.5rem', color: 'white' }}>{item.icon}</div>
                                <div style={{ display: isOpen ? 'block' : 'none' }}>{item.name}</div>
                            </NavLink>
                        ))}
                    </div>
                </div>
                <main style={{ flex: 1 }}>{children}</main>
            </div>
        </div>
    );
};

export default Sidebar;
