import React, { useState } from 'react';
import {
    FaTh,
    FaBars,
    FaUserAlt,
    FaRegChartBar,
    FaCommentAlt,
    FaShoppingBag,
    FaThList
} from "react-icons/fa";
import { NavLink } from 'react-router-dom';

const Sidebar = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    const menuItem = [
        {
            path: "/",
            name: "Dashboard",
            icon: <FaTh />
        },
        {
            path: "/about",
            name: "About",
            icon: <FaUserAlt />
        },
        {
            path: "/analytics",
            name: "Analytics",
            icon: <FaRegChartBar />
        },
        {
            path: "/comment",
            name: "Comment",
            icon: <FaCommentAlt />
        },
        {
            path: "/product",
            name: "Product",
            icon: <FaShoppingBag />
        },
        {
            path: "/productList",
            name: "Product List",
            icon: <FaThList />
        }
    ];

    return (
        <div className="container" style={{ display: 'flex' }}>
            <div style={{ width: isOpen ? "200px" : "50px", transition: "width 0.3s", background: "white", boxShadow: "3px -3px 20px rgba(0, 0, 0, 0.20) inset" }} className="sidebar">
                <div className="top_section" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px' }}>
                    <h1 style={{ display: isOpen ? "block" : "none", fontSize: "1.5rem", fontWeight: "bold" }} className="logo">Logo</h1>
                    <div style={{ marginLeft: isOpen ? "50px" : "0px" }} className="bars">
                        <FaBars onClick={toggle} />
                    </div>
                </div>
                {
                    menuItem.map((item, index) => (
                        <NavLink to={item.path} key={index} className="link" activeClassName="active" style={{ display: 'flex', alignItems: 'center', padding: '10px 20px', textDecoration: 'none', color: '#003E1E' }}>
                            <div className="icon" style={{ marginRight: isOpen ? "10px" : "0px" }}>{item.icon}</div>
                            <div style={{ display: isOpen ? "block" : "none" }} className="link_text">{item.name}</div>
                        </NavLink>
                    ))
                }
            </div>
            <main style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
                <div style={{ flex: '1 1 0', height: 700, background: 'white', justifyContent: 'flex-start', alignItems: 'flex-start', gap: 10, display: 'flex' }}>
                    <div style={{ flex: '1 1 0', height: 63, background: 'linear-gradient(90deg, #00A450 0%, #003E1E 100%)' }} />
                    <div style={{ width: 1202, height: 63, textAlign: 'center', color: 'white', fontSize: 30, fontFamily: 'JioType Var', fontWeight: '500', wordWrap: 'break-word' }}>ECalibration</div>
                </div>
                {children}
            </main>
        </div>
    );
};

export default Sidebar;
