import React, { useState } from 'react';
import {
    FaTh,
    FaBars,
    FaUserAlt,
    FaRegChartBar,
    FaCommentAlt,
    FaShoppingBag,
    FaThList
} from "react-icons/fa";
import { NavLink, useLocation } from 'react-router-dom';

const Sidebar = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);

    const location = useLocation();

    const menuItem = [
        {
            path: "/",
            name: "Dashboard",
            icon: <FaTh />
        },
        {
            path: "/about",
            name: "About",
            icon: <FaUserAlt />
        },
        {
            path: "/analytics",
            name: "Analytics",
            icon: <FaRegChartBar />
        },
        {
            path: "/comment",
            name: "Comment",
            icon: <FaCommentAlt />
        },
        {
            path: "/product",
            name: "Product",
            icon: <FaShoppingBag />
        },
        {
            path: "/productList",
            name: "Product List",
            icon: <FaThList />
        }
    ];

    // Get the current page name based on the path
    const currentPageName = menuItem.find(item => item.path === location.pathname)?.name || "ECalibration";

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
            {/* Navbar */}
            <div
                style={{
                    width: '100%',
                    height: '63px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    background: '#003E1E',
                    color: 'white',
                    padding: '0 16px',
                    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                    marginLeft: isOpen ? '259px' : '50px',  // Dynamic left margin based on sidebar width
                    transition: 'margin-left 0.3s',  // Smooth transition when sidebar opens/closes
                    flexShrink: 0,  // Prevents title from shrinking
                    width: 'calc(100% - ' + (isOpen ? '259px' : '50px') + ')',  // Title occupies the remaining space
                }}
            >
                <FaBars
                    onClick={toggle}
                    style={{
                        cursor: 'pointer',
                        fontSize: '1.5rem',
                        color: 'white',
                    }}
                />
                <div
                    style={{
                        textAlign: 'center',
                        color: 'white',
                        fontSize: '30px',
                        fontFamily: 'JioType Var',
                        fontWeight: '500',
                        wordWrap: 'break-word',
                        width: '100%',
                    }}
                >
                    {currentPageName}
                </div>
                <div style={{ width: '24px' }}></div> {/* Placeholder for alignment */}
            </div>

            {/* Sidebar and Main Content */}
            <div style={{ display: 'flex', flex: 1 }}>
                <div
                    style={{
                        width: isOpen ? '259px' : '50px',
                        height: '100%',
                        background: 'white',
                        boxShadow: '3px -3px 20px rgba(0, 0, 0, 0.20) inset',
                        position: 'relative',
                        transition: 'width 0.3s',
                    }}
                >
                    <div style={{ padding: '16px', display: 'flex', alignItems: 'center' }}>
                        <img
                            style={{ width: '182px', height: '91px', display: isOpen ? 'block' : 'none' }}
                            src="https://via.placeholder.com/182x91"
                            alt="Logo"
                        />
                    </div>
                    <div>
                        {menuItem.map((item, index) => (
                            <NavLink
                                to={item.path}
                                key={index}
                                style={{
                                    textDecoration: 'none',
                                    display: 'flex',
                                    alignItems: 'center',
                                    padding: '10px 30px',
                                    color: 'white',
                                    fontSize: '20px',
                                    fontFamily: 'JioType Var',
                                    fontWeight: '400',
                                    background: 'linear-gradient(90deg, #00A450 0%, #003E1E 100%)',
                                    marginBottom: '10px',
                                    borderRadius: '4px',
                                    transition: 'all 0.3s',
                                    gap: isOpen ? '10px' : '0',
                                }}
                                activeClassName="active"
                            >
                                <div style={{ fontSize: '1.5rem', color: 'white' }}>{item.icon}</div>
                                <div style={{ display: isOpen ? 'block' : 'none' }}>{item.name}</div>
                            </NavLink>
                        ))}
                    </div>
                </div>
                <main style={{ flex: 1 }}>{children}</main>
            </div>
        </div>
    );
};

export default Sidebar;
