import React, { useState } from 'react';
import {
    FaTh,
    FaBars,
    FaUserAlt,
    FaRegChartBar,
    FaCommentAlt,
    FaShoppingBag,
    FaThList
} from "react-icons/fa";
import { NavLink } from 'react-router-dom';

const Sidebar = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);

    const menuItem = [
        {
            path: "/",
            name: "Dashboard",
            icon: <FaTh />
        },
        {
            path: "/about",
            name: "About",
            icon: <FaUserAlt />
        },
        {
            path: "/analytics",
            name: "Analytics",
            icon: <FaRegChartBar />
        },
        {
            path: "/comment",
            name: "Comment",
            icon: <FaCommentAlt />
        },
        {
            path: "/product",
            name: "Product",
            icon: <FaShoppingBag />
        },
        {
            path: "/productList",
            name: "Product List",
            icon: <FaThList />
        }
    ];

    return (
        <div style={{ display: 'flex' }}>
            <div
                style={{
                    width: isOpen ? '259px' : '50px',
                    height: '100vh',
                    background: 'white',
                    boxShadow: '3px -3px 20px rgba(0, 0, 0, 0.20) inset',
                    position: 'relative',
                    transition: 'width 0.3s',
                }}
            >
                <div style={{ padding: '16px', display: 'flex', alignItems: 'center' }}>
                    <img
                        style={{ width: '182px', height: '91px', display: isOpen ? 'block' : 'none' }}
                        src="https://via.placeholder.com/182x91"
                        alt="Logo"
                    />
                    <FaBars
                        onClick={toggle}
                        style={{ cursor: 'pointer', marginLeft: isOpen ? 'auto' : '0', fontSize: '1.5rem' }}
                    />
                </div>
                <div>
                    {menuItem.map((item, index) => (
                        <NavLink
                            to={item.path}
                            key={index}
                            style={{
                                textDecoration: 'none',
                                display: 'flex',
                                alignItems: 'center',
                                padding: '10px 20px',
                                color: 'white',
                                fontSize: '20px',
                                fontFamily: 'JioType Var',
                                fontWeight: '400',
                                background: 'linear-gradient(90deg, #00A450 0%, #003E1E 100%)',
                                marginBottom: '10px',
                                borderRadius: '4px',
                                transition: 'all 0.3s',
                                gap: isOpen ? '10px' : '0',
                            }}
                            activeClassName="active"
                        >
                            <div style={{ fontSize: '1.5rem', color: 'white' }}>{item.icon}</div>
                            <div style={{ display: isOpen ? 'block' : 'none' }}>{item.name}</div>
                        </NavLink>
                    ))}
                </div>
                <div
                    style={{
                        position: 'absolute',
                        bottom: '20px',
                        left: '20px',
                        width: isOpen ? '176px' : '50px',
                        display: 'flex',
                        alignItems: 'center',
                        padding: '10px',
                        background: 'white',
                        boxShadow: '0px 3.6px 3.6px rgba(0, 0, 0, 0.25) inset',
                        borderRadius: '9px',
                        border: '0.9px black solid',
                        gap: isOpen ? '10px' : '0',
                        transition: 'all 0.3s',
                        cursor: 'pointer',
                    }}
                >
                    <div
                        style={{
                            width: '27px',
                            height: '27px',
                            background: 'black',
                            borderRadius: '50%',
                        }}
                    ></div>
                    <div
                        style={{
                            fontSize: '18px',
                            fontFamily: 'JioType Var',
                            fontWeight: '400',
                            display: isOpen ? 'block' : 'none',
                        }}
                    >
                        Logout
                    </div>
                </div>
            </div>
            <main style={{ flex: 1 }}>{children}</main>
        </div>
    );
};

export default Sidebar;
